/* eslint-disable max-len */
/* eslint-disable react/react-in-jsx-scope */
import { useState, useEffect, useContext } from "react";
import { useParams } from "react-router-dom";
import Box from "@mui/material/Box";
import { Paper, Typography, Button } from "@mui/material";

import useAxios from "../utils/useAxios";
import TopBar from "../layout/TopBar";

import Grid2 from "@mui/material/Unstable_Grid2/Grid2";

import LoadContext from "../context/LoadContext";
import InvitacionForm from "../components/InvitacionForm";
import TiempoCard from "../components/TiempoCard";
import ContrasenaCard from "../components/ContrasenaCard";
import ParticipantesCard from "../components/ParticipantesCard";
import Invitados from "../components/Invitados";

// import ModalElimProd from '../components/ModalElimProd';

import Alerta from "../components/Alerta";

export default function Solicitud() {
  const { id } = useParams();
  const [data, setData] = useState();
  const [open, setOpen] = useState();
  const [pk, setPk] = useState();
  const [invitados, setInvitados] = useState([]);

  const { post, alert, setAlert } = useContext(LoadContext);

  const api = useAxios();

  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await api.get(`/getsinglesoli/?id=${id}`);
        setData(response.data);
      } catch {
        setData("Algo salió mal");
      }
    };

    fetchData();
  }, []);

  return (
    <Box
      sx={{
        minHeight: "100vh",
        backgroundColor: "#ededed",
        paddingLeft: 8,
        paddingTop: 8,
      }}
    >
      <TopBar />

      <Grid2
        container
        spacing={2}
        sx={{ justifyContent: "center", paddingTop: 3, margin: 2 }}
      >
        <Grid2
          xs={12}
          sm={4}
          sx={{
            display: "flex",
            flexDirection: "column",
            justifyContent: "center",
            alignItems: "center",
          }}
        >
          <TiempoCard data={data} />
        </Grid2>
        <Grid2
          xs={12}
          sm={4}
          sx={{
            display: "flex",
            flexDirection: "column",
            justifyContent: "center",
            alignItems: "center",
          }}
        >
          <ContrasenaCard data={data} />
        </Grid2>
        <Grid2
          xs={12}
          sm={4}
          sx={{
            display: "flex",
            flexDirection: "column",
            justifyContent: "center",
            alignItems: "center",
          }}
        >
          <ParticipantesCard data={data} />
        </Grid2>
        <Grid2
          xs={12}
          sm={12}
          sx={{
            display: "flex",
            flexDirection: "column",
            justifyContent: "center",
            alignItems: "center",
          }}
        >
          <Paper sx={{ width: "100%" }}>
            <Grid2 container spacing={1} sx={{ justifyContent: "center" }}>
              <Grid2 xs={12} sm={5}
              sx={{
                display: "flex",
                flexDirection: "column",
                justifyContent: "center",
                alignItems: "center",
              }}>
                <InvitacionForm data={data} setInvitados={setInvitados} />
              </Grid2>

              <Invitados invitados={invitados} />
            </Grid2>
          </Paper>
        </Grid2>
      </Grid2>
    </Box>
  );
}
