import React, { useContext, useState, useEffect } from 'react';
import {Link as RouterLink} from 'react-router-dom'
import Box from '@mui/material/Box';
import Typography from '@mui/material/Typography';
import { Paper, IconButton, Link } from '@mui/material';
import Grid from '@mui/material/Grid';
import AuthContext from '../context/AuthContext';
import LoadContext from '../context/LoadContext';
import TopBar from '../layout/TopBar';
import CheckCircleIcon from '@mui/icons-material/CheckCircle';
import CancelIcon from '@mui/icons-material/Cancel';
import useAxios from '../utils/useAxios';
import Datatable from '../components/Tabla';


export default function Home() {

  const [res, setRes] = useState('')
 

  const { user } = useContext(AuthContext);

  const {
    loading, 
    setLoading, 
    post,
    setPost, 
    alert, 
    setAlert,
      } = useContext(LoadContext);

  const api = useAxios();

  useEffect(()=>{
    const fetchSolicitudes = async() => {
      try {
        const response = await api.get('/getmisinvis/',{
          params: {'id':user.user_id}
        })
        
        console.log(response.data)

        const invitaciones = response.data.map((e)=>{
          if (e.estado === 'v'){
            e.estado = 'vigente'
          }
        
          const dict= {
            id: e.id,
            evento: e.solicitud.evento,
            solicitante: e.solicitud.solicitante.username,
            inicio: new Date(e.solicitud.horaIni),
            fin: new Date(e.solicitud.horaTer),
            estado: e.estado
          }
          return dict;
          
          })
        setRes(invitaciones);
      }catch{
        console.log("error")
      }
    }
    fetchSolicitudes();
    
  },[post])

  function getInicioFin(params) {
    const inicio = params.row.inicio.toString().split(" ")
    const final = params.row.fin.toString().split(" ")

    return `${inicio[4]} ${final[4]}`;
  }

  const handleAceptar = async (params) => {
    try {
      const response = await api.patch(
        
        '/invitacion/',
        { estado: 'a',
       },
        {
          params: {
            id: params.id,
          },
        },
      ).then(
        setPost(true),
        setAlert(true)
      ).catch((error) => {
        if (error.response) {
          console.log(error.response.data);
        }
      });
    } catch {
      console.log('wena');
    }
    
    setPost(false);
  };
  const handleRechazar = async (params) => {

    try {
      const response = await api.patch(
        '/invitacion/',
        { estado: 'r',
       },
        {
          params: {
            id: params.id,
          },
        },
      ).then(
        setPost(true),
        setAlert(true)
      ).catch((error) => {
        if (error.response) {
          console.log(error.response.data);
        }
      });
    } catch {
      console.log('wena');
    }
    setPost(false)
  };


 

  const columns = [
    { 
      field: 'id', 
      headerName: 'id', 
      flex: 0.5,
      align: 'center',
      headerAlign: 'center',
     },

    {
      field: 'solicitante',
      headerName: 'Solicitante',
      flex: 1,
      align: 'center',
      headerAlign: 'center',
    },
    {
      field: 'evento',
      headerName: 'evento',
      type: 'string',
      flex: 1,
      align: 'center',
      headerAlign: 'center',
    },
    {
      field: 'inicio',
      headerName: 'fecha',
      type: 'date',
      flex: 1,
      alignItems: 'center',
      align: 'center',
      headerAlign: 'center',
    },
    {
      field: 'fin',
      headerName: 'inicio - fin',
      type: 'dateTime',
      flex: 1,
      alignItems: 'center',
      align: 'center',
      headerAlign: 'center',
      valueGetter: getInicioFin,
      renderCell: (params)=>{
        const text = params.value.split(" ");
        return (<Box sx={{display:"flex",flexDirection:"column"}}>
            <Typography  sx={{fontSize:12}}>{text[0]}</Typography>
            <Typography sx={{fontSize:12}}>{text[1]}</Typography>
        </Box>)
        
      }
    },
    {
      field: 'acciones',
      headerName: 'Acciones',
      flex: 1,
      alignItems: 'center',
      align: 'center',
      headerAlign: 'center',
      renderCell: (params) => (
        
        <Box sx={{ display: 'flex', mx: 'auto' }}>
          <IconButton color="success" aria-label="Aceptar" onClick={()=> handleAceptar(params.row)}>
            <CheckCircleIcon />
          </IconButton> 
        
          <IconButton color="primary" aria-label="Rechazar"  onClick={()=> handleRechazar(params.row)}>
            <CancelIcon />
          </IconButton>
          
        </Box>

      ),

    },
  ];

  let rows = [];
  if (res) {
    rows = res;
  }



  return (
    <Box
      sx={{
        minHeight: '100vh',
        backgroundColor: '#ededed',
        paddingLeft: 8,
        paddingTop: 8,
      }}
    >
      <TopBar />
      <Grid container sx={{ justifyContent: 'center', paddingTop: 3 }}>
        <Grid
          item
          xs={12}
          sm={10}
          sx={{
            display: 'flex',
            flexDirection: 'column',
            justifyContent: 'center',
            alignItems: 'center',
          }}
        >
          <Paper
            sx={{
              height: '10vh',
              width: '100%',
              padding: 2,
            }}
          >
            <Typography variant="h5">
              wena wena
              {user.username}
            </Typography>
          </Paper>
          <Paper
              sx={{
                width: '100%',
                padding: 2,
                marginTop: 3,
              }}
            >
              <Box
                sx={{ mx: 2, display: 'flex', justifyContent: 'space-between' }}
              >
                <Typography variant="h5">Solicitudes</Typography>
              </Box>
              {res ? <Datatable columns={columns} rows={rows} /> : null }
            </Paper>
        </Grid>
      </Grid>
    </Box>
  );
}
