/* eslint-disable react/jsx-props-no-spreading */
/* eslint-disable react/react-in-jsx-scope */
import { useContext, useState, useEffect } from 'react';
import {
  Autocomplete, TextField, Button, Typography,
} from '@mui/material';
import Box from '@mui/material/Box';
import Card from '@mui/material/Card';
import { CardActionArea, CardHeader, CardContent } from '@mui/material';
import * as yup from 'yup';
import { useFormik } from 'formik';
import useAxios from '../utils/useAxios';
import LoadContext from '../context/LoadContext';

// eslint-disable-next-line react/prop-types
export default function InvitacionForm({data, agregarInvi}) {
  const { setPost, setAlert } = useContext(LoadContext);
  const [users, setUsers] = useState([])
  
  // const navigate = useNavigate();

  // const { loginUser } = useContext(AuthContext);

  const api = useAxios();

  useEffect(()=>{

      const getEstu = async ()=>{
        const response = await api.get('/getestudiantes/')
        setUsers(response.data)
    }
   
   
    getEstu()
  },[setUsers])

  const HandleSubmit = (valores) => {
    
    console.log(valores)
    
  };

  const validationSchema = yup.object({

    cant: yup
      .number('Ingrese cantidad de useructos')
      .integer('el valor debe ser un número entero')
      .positive('El valor debe ser positivo')
      .required('la cantidad es obligatoria'),

  });

  const formik = useFormik({
    initialValues: {
      user_id: {
        id: null, nombre: '', email: '', precio: 0,
      },
      cant: 0,
    },
    validationSchema,
    onSubmit: (values) => {
      HandleSubmit(values);
      
     
    },
  });

  return (
    <Card sx={{width:'100%'}}>
      
      <CardContent>
      <form onSubmit={formik.handleSubmit}>
        <Box sx={{ display: 'flex', flexDirection: 'column'}}>
        <Typography  sx={{marginRight: 1}}>Correo INACAP</Typography>
          {users ? (
            <Autocomplete
              id="user_id"
              name="user_id"
              options={users}
              getOptionLabel={(option) => option.email}
              style={{ width: '100%' }}
              onChange={(e, value) => {
                formik.setFieldValue(
                  'user_id',
                  value !== null ? value : formik.initialValues.user_id,
                );
              }}
              renderInput={(params) => (
                <TextField
                  margin="normal"
                  label="estudiantes"
                  fullWidth
                  name="user_id"
                  {...params}
                />
              )}
            />
          ) : null}

          <Button variant="contained" color="primary" sx={{ marginTop: 2 }} type="submit">Agregar</Button>

        </Box>
      </form>
      </CardContent>
    </Card>

  );
}
