import React from "react";
import { Box, Typography, Chip, Stack,Checkbox,FormControlLabel, Paper } from "@mui/material";
import CancelIcon from '@mui/icons-material/Cancel';


export default function Invitados({invitados, agregarInvi }) {

    const handleDelete = (inv)=>{
        let arr = invitados.filter(invitados => invitados !== inv)
        agregarInvi(arr)
    }
  return (
    <Box sx={{width: '100%', display:'flex', flexDirection:'column',alignItems:'center'}}>
        
      {invitados.length < 1 ? <Typography>Sin Destinatarios</Typography> : null}
      <Paper>
      <Stack direction="column" spacing={1} sx={{maxHeight: '150px', width:'100%',overflow: 'auto', display:'flex',flexDirection: 'column', alignItems:'center'}}>
         {invitados ? invitados.map((inv)=>{
           return <Chip size='small'color="primary" sx={{width:'60%'}} label={inv.user_id.email} onDelete={()=>handleDelete(inv)}/>
        }): null}
      </Stack>
      </Paper>
      {invitados && invitados.length > 1 ? <FormControlLabel control={<Checkbox defaultChecked />} label="Los datos son correctos" /> : null }
     

    </Box>
  );
}
