/* eslint-disable react/jsx-props-no-spreading */
/* eslint-disable react/react-in-jsx-scope */
import { useContext, useState, useEffect } from 'react';
import {
  Autocomplete, TextField, Typography, Button
} from '@mui/material';
import Box from '@mui/material/Box';
import * as yup from 'yup';
import { useFormik } from 'formik';
import useAxios from '../utils/useAxios';
import LoadContext from '../context/LoadContext';
import PersonAddAlt1Icon from '@mui/icons-material/PersonAddAlt1';

// eslint-disable-next-line react/prop-types
export default function InvitacionForm({data, agregarInvi, invitados}) {
  const { setPost, setAlert } = useContext(LoadContext);
  const [users, setUsers] = useState([])
  const [option, setOption] = useState([])
  const [message, setMessage] = useState('');
  
  // const navigate = useNavigate();

  // const { loginUser } = useContext(AuthContext);

  const api = useAxios();

  
  const handleKeyDown = event => {
    console.log(event.key);

    if (event.key === 'Enter') {
      event.preventDefault();

      // ðŸ‘‡ï¸ access input value from state
      console.log(message);

      // ðŸ‘‡ï¸ access input value from event object
      // console.log(event.target.value)

      console.log('User pressed Enter âœ…');
    }
  };

  const filtrar = ()=>{
    let arr = ['wena']
    console.log(this.state)
    return arr
  }
  
  

  useEffect(()=>{

      const getEstu = async ()=>{
        const response = await api.get('/getestudiantes/')
        setUsers(response.data)
    }
   
    getEstu()
  },[setUsers])

  const HandleSubmit = (valores) => {
    
    
    if(invitados){
    let arrInvi = [...invitados,valores]
   
    console.log(arrInvi)
    agregarInvi(arrInvi)
    }
  };
  const validationSchema = yup.object({
    

  });

  const formik = useFormik({
    initialValues: {
      user_id: {
        id: null, nombre: '', email: '', precio: 0,
      }
    },
    validationSchema,
    onSubmit: (values) => {
      HandleSubmit(values);
      
     
    },
  });

  return (
    <Box sx={{width: '100%', padding: 2}}>
      <form onSubmit={formik.handleSubmit}>
        
        <Box sx={{display:'flex', flexDirection:'column', alignItems:'center', justiItems:'center', width:'100%'}}>
          {users ? (
            <Autocomplete
              id="user_id"
              name="user_id"
              options = {option}
              filterOptions = {(x)=>x}
              getOptionLabel={(option) => option.email}
              style ={{width: '100%'}}
              
              // onChange={(e,value) => {
              //   formik.setFieldValue(
              //     'user_id',
              //     value !== null ? value : formik.initialValues.user_id,
                  
              //   );
              // }}
              onChange={(e,value) => {
                
              formik.setFieldValue(
                'user_id',
                value !== null ? value: formik.initialValues.user_id,
              )
              
              }
              }
            
              renderInput={(params) => (
                
                <TextField
                  margin="normal"
                  label="estudiantes"
                  fullWidth
                  name="user_id"
                  onChange={(e)=>console.log(e.target.value)}
                  {...params}
                />
              )}
            />
          ) : null}

<Button color='primary' variant='contained' type="submit" endIcon={<PersonAddAlt1Icon color='secondary' />}> Agregar</Button>
          </Box>
          

        
      </form>
      </Box>


  );
}
