/* eslint-disable react/jsx-props-no-spreading */
/* eslint-disable react/react-in-jsx-scope */
import { useContext, useState, useEffect, useRef } from 'react';
import {
  Autocomplete, TextField, Typography, Button
} from '@mui/material';
import Box from '@mui/material/Box';
import  * as yup from 'yup'
import { useFormik } from 'formik';
import useAxios from '../utils/useAxios';
import LoadContext from '../context/LoadContext';
import PersonAddAlt1Icon from '@mui/icons-material/PersonAddAlt1';
import { applyInitialState } from '@mui/x-data-grid/hooks/features/columns/gridColumnsUtils';

// eslint-disable-next-line react/prop-types
export default function InvitacionForm({data, agregarInvi, invitados}) {
  const { setPost, setAlert } = useContext(LoadContext);
  const [users, setUsers] = useState([])
  const [option, setOption] = useState([])
  const [message, setMessage] = useState('');
  
  // const navigate = useNavigate();

  // const { loginUser } = useContext(AuthContext);

  const api = useAxios();

  const textRef = useRef()
  const showRefText = () =>{
    console.log(textRef)
    textRef.current.value = ""
  }
  const filtrar = async (e)=>{

    if(e.target.value){
      let url = `/getestemail/?search=${e.target.value}`
      const response = await api.get(url)
      setOption(response.data)
    }
  }

  const reiniciar = (e)=>{
    e.preventDefault()

    if(e.target.value){
      
      e.target.value = ""
    }
  }
  
  
  

  useEffect(()=>{

      const getEstu = async ()=>{
        const response = await api.get('/getestudiantes/')
        setUsers(response.data)
    }
   
    getEstu()
  },[setUsers])

  const HandleSubmit = (valores) => {
    
    
    if(invitados){
    let arrInvi = [...invitados,valores]
   
    console.log(arrInvi)
    agregarInvi(arrInvi)
    }
  };

 
const validationSchema = yup.object({
  user_id: yup.object({
    id: yup.number().required(),
    username: yup.string().required(),
    email: yup.string().email("Ingresa un correo inacap válido").required("Campo Obligatorio")

  })
 
})
 

  const formik = useFormik({
    initialValues: {
      user_id: {
        id: null, username: '', email: '',
      }
    },
    validationSchema,
    onSubmit: (values) => {
      showRefText();
      HandleSubmit(values);
      formik.resetForm({values:{
        user_id: ""
     
      }});
      
     
    },
  });

  return (
    <Box sx={{width: '100%', padding: 2}}>
      <form onSubmit={formik.handleSubmit}>
        
        <Box sx={{display:'flex', flexDirection:'column', alignItems:'center', justiItems:'center', width:'100%'}}>
          {users ? (
            <Autocomplete
          
              id="user_id"
              name="user_id"
              options = {option}
              filterOptions = {(x)=>x}
              getOptionLabel={(option) => option.email}
              style ={{width: '100%'}}
              clearOnBlur={true}
              value={null}
              
              
              
              // onChange={(e,value) => {
              //   formik.setFieldValue(
              //     'user_id',
              //     value !== null ? value : formik.initialValues.user_id,
                  
              //   );
              // }}
              onChange={(e,value) => {
                
              formik.setFieldValue(
                'user_id',
                value !== null ? value: formik.initialValues.user_id,
              )
              
              }
              }
            
              renderInput={(params) => (
                
                <TextField
                  
                  margin="normal"
                  label="estudiantes"
                  fullWidth
                  name="email"
                  inputRef = {textRef}
                  onChange={(e)=>filtrar(e)}
                  {...params}
                  error={formik.touched.email && Boolean(formik.errors.email)}
                  onSubmit ={(e)=>reiniciar(e)}
                  
                  
                />
              )}
            />
          ) : null}

<Button color='primary' variant='contained' type="submit" endIcon={<PersonAddAlt1Icon color='secondary' />}> Agregar</Button>
          </Box>
          

        
      </form>
      </Box>


  );
}
